' ',
'  l_css_code      gt_string;',
'  l_onload_code   gt_string;   ',
'begin',
'',
'    ----- Set CSS -----------',
'    l_css_code := l_style; ',
'    ',
'    if l_css_icon is null then ',
'        l_css_code := l_css_code||''.srch_nav input{padding: 3px 2px 3px 3px;}'';',
